'use strict';

// MODULES //

var tape = require( 'tape' );
var isSafeInteger = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isSafeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a primitive number having a safe integer value', function test( t ) {
	t.strictEqual( typeof isSafeInteger.isPrimitive, 'function', 'export is a function' );
	t.end();
});

tape( 'attached to the main export is a method to test for a number object having a safe integer value', function test( t ) {
	t.strictEqual( typeof isSafeInteger.isObject, 'function', 'export is a function' );
	t.end();
});
