'use strict';

// MODULES //

var tape = require( 'tape' );
var isSafeInteger = require( './../lib/object.js' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isSafeInteger, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a number object having a safe integer value', function test( t ) {
	t.strictEqual( isSafeInteger( new Number( 5 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if provided a number object not having a safe integer value', function test( t ) {
	t.strictEqual( isSafeInteger( new Number( 2.0e200 ) ), false, 'returns false' );
	t.end();
});

tape( 'the function returns `false` if provided a primitive number, even if the number is a safe integer value', function test( t ) {
	t.strictEqual( isSafeInteger( 3.0 ), false, 'returns false' );
	t.end();
});

tape( 'function returns `false` if not provided a number', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		null,
		true,
		undefined,
		[],
		{},
		new Date(),
		/./,
		new RegExp( '.' ),
		function(){}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isSafeInteger( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
