/* eslint-disable no-new-wrappers */
'use strict';

var isStringArray = require( './../lib' );


var bool = isStringArray( [ 'Hello World!' ] );
console.log( bool );
// => true

bool = isStringArray( [ 'a', 'b', 'c' ] );
console.log( bool );
// => true

bool = isStringArray( [ new String( 'abc' ), 'def', 'ghi' ] );
console.log( bool );
// => true

bool = isStringArray( 'abc' );
console.log( bool );
// => false

bool = isStringArray( [] );
console.log( bool );
// => false

bool = isStringArray( [ 'a', NaN ] );
console.log( bool );
// => false
