/* eslint-disable no-restricted-syntax, no-empty-function */
'use strict';

var hasSymbols = require( '@stdlib/utils/detect-symbol-support' )();
var isSymbol = require( './../lib' );

var bool;

if ( hasSymbols ) {
	bool = isSymbol( Symbol( 'beep' ) );
	console.log( bool );
	// => true
} else {
	console.log( 'Environment does not support symbols.' );
}
bool = isSymbol( 'beep' );
console.log( bool );
// => false

bool = isSymbol( {} );
console.log( bool );
// => false

bool = isSymbol( [] );
console.log( bool );
// => false

bool = isSymbol( null );
console.log( bool );
// => false

bool = isSymbol( void 0 );
console.log( bool );
// => false

bool = isSymbol( true );
console.log( bool );
// => false

bool = isSymbol( function foo() {} );
console.log( bool );
// => false
