'use strict';

var isTruthyArray = require( './../lib' );

var bool = isTruthyArray( [ null, '', NaN, 0, void 0, false ] );
console.log( bool );
// => false

bool = isTruthyArray( [ [], {}, true, 'a', 5, -5 ] );
console.log( bool );
// => true

bool = isTruthyArray( [] );
console.log( bool );
// => false

bool = isTruthyArray( true );
console.log( bool );
// => false

bool = isTruthyArray( {} );
console.log( bool );
// => false
