'use strict';

/**
* Test if a value is truthy.
*
* @module @stdlib/assert/is-truthy
*
* @example
* var isTruthy = require( '@stdlib/assert/is-truthy' );
*
* var bool = isTruthy( false );
* // returns false
*
* bool = isTruthy( null );
* // returns false
*
* bool = isTruthy( '' );
* // returns false
*
* bool = isTruthy( 0 );
* // returns false
*
* bool = isTruthy( void 0 );
* // returns false
*
* bool = isTruthy( NaN );
* // returns false
*
* bool = isTruthy( [] );
* // returns true
*/

// MODULES //

var isTruthy = require( './is_truthy.js' );


// EXPORTS //

module.exports = isTruthy;
