'use strict';

var isTypedArrayLength = require( './../lib' );

console.log( isTypedArrayLength( 5 ) );
// => true

console.log( isTypedArrayLength( 0 ) );
// => true

console.log( isTypedArrayLength( 3.0e200 ) );
// => false

console.log( isTypedArrayLength( 5.256 ) );
// => false

console.log( isTypedArrayLength( 1.0/0.0 ) );
// => false

console.log( isTypedArrayLength( -1.0/0.0 ) );
// => false

console.log( isTypedArrayLength( NaN ) );
// => false

console.log( isTypedArrayLength( '5' ) );
// => false

console.log( isTypedArrayLength( null ) );
// => false
