'use strict';

// MODULES //

var tape = require( 'tape' );
var isTypedArrayLike = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof isTypedArrayLike, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a typed-array-like object', function test( t ) {
	var values;
	var i;

	values = [
		new Int8Array(),
		new Uint8Array(),
		new Int16Array(),
		new Uint16Array(),
		new Int32Array(),
		new Uint32Array(),
		new Float32Array(),
		new Float64Array(),
		new Uint8ClampedArray(),
		{
			'length': 10,
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': 0,
			'byteLength': 10
		}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isTypedArrayLike( values[i] ), true, 'returns true when provided '+values[i] );
	}

	t.end();
});

tape( 'the function returns `false` if not provided a typed-array-like object', function test( t ) {
	var values;
	var i;

	values = [
		'beep',
		5,
		null,
		undefined,
		NaN,
		true,
		false,
		{},
		[],
		function boop() {},
		{
			'length': 3.14,
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': 0,
			'byteLength': 10
		},
		{
			'length': 10,
			'BYTES_PER_ELEMENT': true,
			'byteOffset': 0,
			'byteLength': 10
		},
		{
			'length': 10,
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': [],
			'byteLength': 10
		},
		{
			'length': 10,
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': 0,
			'byteLength': null
		},
		{
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': 0,
			'byteLength': 10
		},
		{
			'length': 10,
			'byteOffset': 0,
			'byteLength': 10
		},
		{
			'length': 10,
			'BYTES_PER_ELEMENT': 4,
			'byteLength': 10
		},
		{
			'length': 10,
			'BYTES_PER_ELEMENT': 4,
			'byteOffset': 0
		}
	];

	for ( i = 0; i < values.length; i++ ) {
		t.equal( isTypedArrayLike( values[i] ), false, 'returns false when provided '+values[i] );
	}
	t.end();
});
