'use strict';

// MODULES //

var tape = require( 'tape' );
var isUint8Array = require( './../lib' );


// TESTS //

tape( 'main export is a function', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof isUint8Array, 'function', 'main export is a function' );
	t.end();
});

tape( 'the function returns `true` if provided a Uint8Array', function test( t ) {
	t.strictEqual( isUint8Array( new Uint8Array( 10 ) ), true, 'returns true' );
	t.end();
});

tape( 'the function returns `false` if not provided a Uint8Array', function test( t ) {
	var values;
	var i;

	values = [
		'5',
		5,
		NaN,
		true,
		null,
		undefined,
		[],
		{},
		function noop() {},
		new Array( 10 ),
		new Float64Array( 10 ),
		new Float32Array( 10 ),
		new Uint32Array( 10 ),
		new Int32Array( 10 ),
		new Uint16Array( 10 ),
		new Int16Array( 10 ),
		new Int8Array( 10 ),
		new Uint8ClampedArray( 10 )
	];

	for ( i = 0; i < values.length; i++ ) {
		t.strictEqual( isUint8Array( values[i] ), false, 'returns false when provided ' + values[i] );
	}
	t.end();
});
