'use strict';

var isEven = require( '@stdlib/assert/is-even' );
var validateArray = require( './../lib' );

var arr1;
var arr2;
var bool;
var i;

arr1 = new Array( 25 );
for ( i = 0; i < arr1.length; i++ ) {
	arr1[ i ] = i;
}

arr2 = new Array( 25 );
for ( i = 0; i < arr2.length; i++ ) {
	arr2[ i ] = 2 * i;
}

bool = validateArray( isEven, arr1 );
console.log( bool );
// => false

bool = validateArray( isEven, arr2 );
console.log( bool );
// => true
