'use strict';

var isEven = require( '@stdlib/assert/is-even' );
var arrayfcn = require( './../lib' );

var arr1;
var arr2;
var bool;
var f;
var i;

arr1 = new Array( 25 );
for ( i = 0; i < arr1.length; i++ ) {
	arr1[ i ] = i;
}

arr2 = new Array( 25 );
for ( i = 0; i < arr2.length; i++ ) {
	arr2[ i ] = 2 * i;
}

f = arrayfcn( isEven );

bool = f( isEven, arr1 );
console.log( bool );
// => false

bool = f( isEven, arr2 );
console.log( bool );
// => true
