/* eslint-disable no-restricted-syntax */
'use strict';

var randu = require( '@stdlib/math/base/random/randu' );
var hypot = require( '@stdlib/math/base/special/hypot' );
var isnan = require( '@stdlib/math/base/utils/is-nan' );
var logStream = require( './../lib/log' );
var createHarness = require( './../lib' ).createHarness;

// Create the benchmark harness:
var bench = createHarness( onFinish );

function onFinish() {
	bench.close();
}

// Write benchmark results to `stdout`:
var log = logStream();
bench.createStream().pipe( log );

// Set benchmark options:
var opts = {
	'iterations': 1e6,
	'repeats': 3
};

// Run benchmarks:
bench( 'hypot', opts, function benchmark( b ) {
	var x;
	var y;
	var h;
	var i;

	b.tic();
	for ( i = 0; i < b.iterations; i++ ) {
		x = (randu()*100.0) - 50.0;
		y = (randu()*100.0) - 50.0;
		h = hypot( x, y );
		if ( h < 0.0 || h > 200.0 ) {
			b.fail( 'something went wrong!' );
		}
	}
	b.toc();

	if ( isnan( h ) ) {
		b.fail( 'something went wrong!' );
	} else {
		b.pass( 'benchmark success!' );
	}
	b.end();
});
