'use strict';

// MODULES //

var isnan = require( '@stdlib/math/base/assert/is-nan' );
var pkg = require( './../package.json' ).name;
var bench = require( './../lib' );


// MAIN //

bench( pkg, function benchmark( b ) {
	var opts;
	var i;

	opts = {
		'iterations': null,
		'repeats': 3,
		'timeout': 60000,
		'skip': false
	};

	i = 0;
	b.tic();

	return next();

	function next() {
		var harness;
		var stream;
		var hopts;

		i += 1;

		if ( i > b.iterations ) {
			b.toc();
			return b.end();
		}
		hopts = {
			'autoclose': true
		};
		harness = bench.createHarness( hopts, onFinish );

		stream = harness.createStream();
		stream.on( 'close', onClose );

		harness( 'test', opts, test );

		function onFinish() {
			b.ok( true, 'benchmark finished' );
			stream.destroy();
		}

		function onClose() {
			next();
		}
	}

	function test( b ) {
		var x;
		var i;

		x = 0;
		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			x += i * 2;
			if ( isnan( x ) ) {
				b.fail( 'something went wrong' );
			}
		}
		b.toc();
		if ( isnan( x ) ) {
			b.fail( 'something went wrong' );
		} else {
			b.pass( 'success!' );
		}
		b.end();
	}
});
