/* eslint-disable no-restricted-syntax */
'use strict';

var bench = require( './../lib' );

var opts = {
	'iterations': 100,
	'repeats': 3
};

bench( 'async', opts, function benchmark( b ) {
	var i = 0;

	b.tic();
	setTimeout( next, 0 );

	function next( error ) {
		if ( error ) {
			return b.fail( error.message );
		}
		i += 1;
		if ( i <= b.iterations ) {
			return setTimeout( next, 0 );
		}
		b.toc();
		b.pass( 'benchmark success!' );
		after();
	}

	function after() {
		setTimeout( onAfter, 0 );
	}

	function onAfter() {
		b.end();
	}
});
