'use strict';

// MAIN //

/**
* Forcefully ends a benchmark.
*
* @private
*/
function exit() {
	/* eslint-disable no-invalid-this */
	/* jshint validthis: true */ // TODO: remove
	if ( !this._ended ) {
		this.fail( 'benchmark exited without ending' );
		this.end();
	}
} // end FUNCTION exit()


// EXPORTS //

module.exports = exit;
