'use strict';

// MAIN //

/**
* Generates a failing assertion.
*
* @private
* @param {string} msg - message
*/
function fail( msg ) {
	/* eslint-disable no-invalid-this */
	/* jshint validthis: true */ // TODO: remove
	this._assert( false, {
		'message': msg,
		'operator': 'fail'
	});
} // end FUNCTION fail()


// EXPORTS //

module.exports = fail;
