'use strict';

// MAIN //

/**
* Generates a passing assertion.
*
* @private
* @param {string} msg - message
*/
function pass( msg ) {
	/* eslint-disable no-invalid-this */
	/* jshint validthis: true */ // TODO: remove
	this._assert( true, {
		'message': msg,
		'operator': 'pass'
	});
} // end FUNCTION pass()


// EXPORTS //

module.exports = pass;
