'use strict';

// MAIN //

/**
* Forcefully exits a benchmark runner.
*
* @private
*/
function exit() {
	/* eslint-disable no-invalid-this */
	/* jshint validthis: true */ // TODO: remove
	var i;
	for ( i = 0; i < this._benchmarks.length; i++ ) {
		this._benchmarks[ i ].exit();
	}
	this.clear();
	this._stream.destroy();
} // end FUNCTION exit()


// EXPORTS //

module.exports = exit;
