'use strict';

// MODULES //

var resolve = require( 'path' ).resolve;
var bench = require( '@stdlib/bench' );
var randu = require( '@stdlib/random/base/randu' );
var pow = require( '@stdlib/math/base/special/pow' );
var isnan = require( '@stdlib/math/base/assert/is-nan' );
var Float64Array = require( '@stdlib/array/float64' );
var Uint8Array = require( '@stdlib/array/uint8' );
var tryRequire = require( '@stdlib/utils/try-require' );
var pkg = require( './../package.json' ).name;
var dasum = require( './../lib/main.js' );
var wasm = require( './../lib/wasm.js' )();


// VARIABLES //

var ndasum = tryRequire( resolve( __dirname, './../lib/native.js' ) );
var opts = {
	'skip': ( ndasum instanceof Error )
};


// FUNCTIONS //

/**
* Creates a benchmark function for measuring vanilla JavaScript interface.
*
* @private
* @param {Float64Array} x - array buffer
* @returns {Function} benchmark function
*/
function createBenchmark1( x ) {
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var y;
		var i;
		var j;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			for ( j = 0; j < x.length; j++ ) {
				x[ j ] = ( randu()*20000.0 ) - 10000.0;
			}
			y = dasum( x.length, x, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}

/**
* Creates a benchmark function for measuring native add-on interface.
*
* @private
* @param {Float64Array} x - array buffer
* @returns {Function} benchmark function
*/
function createBenchmark2( x ) {
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var y;
		var i;
		var j;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			for ( j = 0; j < x.length; j++ ) {
				x[ j ] = ( randu()*20000.0 ) - 10000.0;
			}
			y = ndasum( x.length, x, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}

/**
* Creates a benchmark function for measuring WASM interface and using heap `setValue` method.
*
* @private
* @param {Uint8Array} bytes - allocated buffer
* @param {PositiveInteger} len - array length
* @param {PositiveInteger} nbytes - number of bytes per element
* @returns {Function} benchmark function
*/
function createBenchmark3( bytes, len, nbytes ) {
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var y;
		var v;
		var i;
		var j;

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			for ( j = 0; j < len; j++ ) {
				v = ( randu()*20000.0 ) - 10000.0;
				bytes.setValue( j * nbytes, v );
			}
			y = wasm( len, bytes, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}

/**
* Creates a benchmark function for measuring WASM interface and using a view.
*
* @private
* @param {Uint8Array} bytes - allocated buffer
* @param {PositiveInteger} len - array length
* @returns {Function} benchmark function
*/
function createBenchmark4( bytes, len ) {
	return benchmark;

	/**
	* Benchmark function.
	*
	* @private
	* @param {Benchmark} b - benchmark instance
	*/
	function benchmark( b ) {
		var view;
		var y;
		var v;
		var i;
		var j;

		view = new Float64Array( bytes.buffer, bytes.byteOffset, len );

		b.tic();
		for ( i = 0; i < b.iterations; i++ ) {
			for ( j = 0; j < len; j++ ) {
				v = ( randu()*20000.0 ) - 10000.0;
				view[ j ] = v;
			}
			y = wasm( len, bytes, 1 );
			if ( isnan( y ) ) {
				b.fail( 'should not return NaN' );
			}
		}
		b.toc();
		if ( isnan( y ) ) {
			b.fail( 'should not return NaN' );
		}
		b.pass( 'benchmark finished' );
		b.end();
	}
}


// MAIN //

/**
* Main execution sequence.
*
* @private
*/
function main() {
	var nbytes;
	var bytes;
	var len;
	var min;
	var max;
	var x;
	var f;
	var i;

	min = 1; // 10^min
	max = 6; // 10^max

	for ( i = min; i <= max; i++ ) {
		len = pow( 10, i );
		x = new Float64Array( len );

		f = createBenchmark1( x );
		bench( pkg+'::memory-reuse,shared-array,main:len='+len, f );

		f = createBenchmark2( x );
		bench( pkg+'::memory-reuse,shared-array,native:len='+len, opts, f );

		nbytes = x.length * x.BYTES_PER_ELEMENT;
		bytes = wasm.malloc( nbytes );
		bytes.set( new Uint8Array( x.buffer ) );

		f = createBenchmark3( bytes, len, x.BYTES_PER_ELEMENT );
		bench( pkg+'::memory-reuse,shared-array,wasm,set-value:len='+len, f );

		f = createBenchmark4( bytes, len );
		bench( pkg+'::memory-reuse,shared-array,wasm,view:len='+len, f );
	}
}

main();
