'use strict';

var round = require( '@stdlib/math/base/special/round' );
var randu = require( '@stdlib/random/base/randu' );
var Float64Array = require( '@stdlib/array/float64' );
var factory = require( './../lib' ).wasm;

var bytes;
var dasum;
var rand;
var sign;
var x;
var i;

// Create a new WASM instance:
dasum = factory();

// Allocate space on the heap:
bytes = dasum.malloc( 100 * 8 ); // 8 bytes per double

// Create a Float64Array view:
x = new Float64Array( bytes.buffer, bytes.byteOffset, 100 );

// Set data on the heap...
for ( i = 0; i < x.length; i++ ) {
	rand = round( randu()*100.0 );
	sign = randu();
	if ( sign < 0.5 ) {
		sign = -1.0;
	} else {
		sign = 1.0;
	}
	x[ i ] = sign * rand;
}
// Compute the sum of absolute values:
console.log( dasum( x.length, bytes, 1 ) );

// Free allocated memory:
dasum.free( bytes );
