/**
* Multiply `X` by a constant and add the result to `Y`.
*
* This file provides an interface to the CBLAS Level 1 routine `cblas_daxpy`.
*
* @see <a href="http://www.netlib.org/lapack/explore-html/de/da4/group__double__blas__level1.html">daxpy</a>
*/
#include "daxpy.h"
#include "daxpy_cblas.h"

/**
* Multiplies `X` by a constant and adds the result to `Y`.
*
* @param N        number of elements
* @param alpha    scalar
* @param X        input array
* @param strideX  X stride length
* @param Y        destination array
* @param strideY  Y stride length
*/
void c_daxpy( const int N, const double alpha, const double *X, const int strideX, double *Y, const int strideY ) {
	cblas_daxpy( N, alpha, X, strideX, Y, strideY );
}
