/**
* Copy values from `X` into `Y`.
*
* This file provides an interface to the CBLAS Level 1 routine `cblas_dcopy`.
*
* @see <a href="http://www.netlib.org/lapack/explore-html/de/da4/group__double__blas__level1.html">dcopy</a>
*/
#include "dcopy.h"
#include "dcopy_cblas.h"

/**
* Copies values from `X` into `Y`.
*
* @param N        number of elements to copy
* @param X        input array
* @param strideX  X stride length
* @param Y        destination array
* @param strideY  Y stride length
*/
void c_dcopy( const int N, const double *X, const int strideX, double *Y, const int strideY ) {
	cblas_dcopy( N, X, strideX, Y, strideY );
}
