/**
* Multiply `X` by a constant and add the result to `Y`.
*
* @see <a href="http://www.netlib.org/lapack/explore-html/df/d28/group__single__blas__level1.html">saxpy</a>
*/
#include "saxpy.h"

/**
* Multiplies `X` by a constant and adds the result to `Y`.
*
* @param N        number of elements
* @param alpha    scalar
* @param X        input array
* @param strideX  X stride length
* @param Y        destination array
* @param strideY  Y stride length
*/
void c_saxpy( const int N, const float alpha, const float *X, const int strideX, float *Y, const int strideY ) {
	int ix;
	int iy;
	int i;
	int m;

	if ( N <= 0 ) {
		return;
	}
	// If `alpha` is `0`, then `y` is unchanged...
	if ( alpha == 0.0 ) {
		return;
	}
	// If both strides are equal to `1`, use unrolled loops...
	if ( strideX == 1 && strideY == 1 ) {
		m = N % 4;

		// If we have a remainder, do a clean-up loop...
		if ( m > 0 ) {
			for ( i = 0; i < m; i++ ) {
				Y[ i ] += alpha * X[ i ];
			}
			if ( N < 4 ) {
				return;
			}
		}
		for ( i = m; i < N; i += 4 ) {
			Y[ i ] += alpha * X[ i ];
			Y[ i+1 ] += alpha * X[ i+1 ];
			Y[ i+2 ] += alpha * X[ i+2 ];
			Y[ i+3 ] += alpha * X[ i+3 ];
		}
		return;
	}
	if ( strideX < 0 ) {
		ix = (1-N) * strideX;
	} else {
		ix = 0;
	}
	if ( strideY < 0 ) {
		iy = (1-N) * strideY;
	} else {
		iy = 0;
	}
	for ( i = 0; i < N; i++ ) {
		Y[ iy ] += alpha * X[ ix ];
		ix += strideX;
		iy += strideY;
	}
	return;
}
