#include "stdlib/blas/ext/base/dsortsh.h"
#include "stdlib/math/base/assert/is_negative_zero.h"
#include "stdlib/math/base/assert/is_nan.h"
#include "stdlib/blas/base/shared.h"
#include "stdlib/strided/base/stride2offset.h"
#include <stdbool.h>

/**
* Sorts a double-precision floating-point strided array using Shellsort.
*
* ## Notes
*
* -   This implementation uses the gap sequence proposed by Ciura (2001).
*
* ## References
*
* -   Shell, Donald L. 1959. "A High-Speed Sorting Procedure." _Communications of the ACM_ 2 (7). Association for Computing Machinery: 30–32. doi:[10.1145/368370.368387](https://doi.org/10.1145/368370.368387).
* -   Ciura, Marcin. 2001. "Best Increments for the Average Case of Shellsort." In _Fundamentals of Computation Theory_, 106–17. Springer Berlin Heidelberg. doi:[10.1007/3-540-44669-9\_12](https://doi.org/10.1007/3-540-44669-9_12).
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        input array
* @param strideX  index increment
*/
void API_SUFFIX(stdlib_strided_dsortsh)( const CBLAS_INT N, const double order, double *X, const CBLAS_INT strideX ) {
	CBLAS_INT ox = stdlib_strided_stride2offset( N, strideX );
	API_SUFFIX(stdlib_strided_dsortsh_ndarray)( N, order, X, strideX, ox );
}

/**
* Sorts a double-precision floating-point strided array using Shellsort and alternative indexing semantics.
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        input array
* @param strideX  index increment
* @param offsetX  starting index
*/
void API_SUFFIX(stdlib_strided_dsortsh_ndarray)( const CBLAS_INT N, const double order, double *X, const CBLAS_INT strideX, const CBLAS_INT offsetX ) {
	CBLAS_INT gap;
	CBLAS_INT ox;
	CBLAS_INT sx;
	CBLAS_INT i;
	CBLAS_INT j;
	CBLAS_INT k;
	double v;
	double u;
	bool flg;

	// Ciura's gap sequence:
	const static int64_t GAPS[] = { 701, 301, 132, 57, 23, 10, 4, 1 };
	const static int64_t NGAPS = 8;

	if ( N <= 0 || order == 0.0 ) {
		return;
	}
	// For a positive stride, sorting in decreasing order is equivalent to providing a negative stride and sorting in increasing order, and, for a negative stride, sorting in decreasing order is equivalent to providing a positive stride and sorting in increasing order...
	if ( order < 0.0 ) {
		sx = -strideX;
		ox = offsetX - ( (N-1) * sx );
	} else {
		sx = strideX;
		ox = offsetX;
	}

	for ( i = 0; i < NGAPS; i++ ) {
		gap = GAPS[ i ];
		for ( j = gap; j < N; j++ ) {
			v = X[ ox+(j*sx) ];

			// If `NaN`, the current value is already sorted to its place...
			if ( stdlib_base_is_nan( v ) ) {
				continue;
			}
			// Perform insertion sort on the "gapped" subarray...
			flg = stdlib_base_is_negative_zero( v );
			for ( k = j; k >= gap; k -= gap ) {
				u = X[ ox+((k-gap)*sx) ];
				if ( u <= v && !(flg && u == v) ) {
					break;
				}
				X[ ox+(k*sx) ] = u;
			}
			X[ ox+(k*sx) ] = v;
		}
	}
	return;
}
