#include "stdlib/blas/ext/base/ssort2hp.h"
#include "stdlib/math/base/assert/is_positive_zerof.h"
#include "stdlib/math/base/assert/is_nanf.h"
#include "stdlib/math/base/special/floor.h"
#include "stdlib/strided/base/stride2offset.h"
#include "stdlib/blas/base/shared.h"

/**
* Simultaneously sorts two single-precision floating-point strided arrays based on the sort order of the first array using heapsort.
*
* ## Notes
*
* -   This implementation uses an in-place algorithm derived from the work of Floyd (1964).
*
* ## References
*
* -   Williams, John William Joseph. 1964. "Algorithm 232: Heapsort." _Communications of the ACM_ 7 (6). New York, NY, USA: Association for Computing Machinery: 347â€“49. doi:[10.1145/512274.512284](https://doi.org/10.1145/512274.512284).
* -   Floyd, Robert W. 1964. "Algorithm 245: Treesort." _Communications of the ACM_ 7 (12). New York, NY, USA: Association for Computing Machinery: 701. doi:[10.1145/355588.365103](https://doi.org/10.1145/355588.365103).
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        first input array
* @param strideX  stride length for `x`
* @param Y        second input array
* @param strideY  stride length for `y`
*/
void API_SUFFIX(stdlib_strided_ssort2hp)( const CBLAS_INT N, const float order, float *X, const CBLAS_INT strideX, float *Y, const CBLAS_INT strideY ) {
	CBLAS_INT ox = stdlib_strided_stride2offset( N, strideX );
	CBLAS_INT oy = stdlib_strided_stride2offset( N, strideY );
	API_SUFFIX(stdlib_strided_ssort2hp_ndarray)( N, order, X, strideX, ox, Y, strideY, oy );
}


/**
* Simultaneously sorts two signle-precision floating-point strided arrays based on the sort order of the first array using Shellsort and alternative indexing semantics.
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        first input array
* @param strideX  stride length for `x`
* @param offsetX  starting index for `x`
* @param Y        second input array
* @param strideY  stride length for `y`
* @param offsetY  starting index for `y`
*/
void API_SUFFIX(stdlib_strided_ssort2hp_ndarray)( const CBLAS_INT N, const float order, float *X, CBLAS_INT strideX, CBLAS_INT offsetX, float *Y, CBLAS_INT strideY, CBLAS_INT offsetY ) {
	CBLAS_INT parent;
	CBLAS_INT child;
	CBLAS_INT sx;
	CBLAS_INT sy;
	CBLAS_INT ox;
	CBLAS_INT oy;
	CBLAS_INT ix;
	CBLAS_INT iy;
	CBLAS_INT n;
	CBLAS_INT j;
	CBLAS_INT k;
	float v1;
	float v2;
	float tx;
	float ty;

	if ( N <= 0 || order == 0.0f ) {
		return;
	}
	// For a positive stride, sorting in decreasing order is equivalent to providing a negative stride and sorting in increasing order, and, for a negative stride, sorting in decreasing order is equivalent to providing a positive stride and sorting in increasing order...
	if ( order < 0.0f ) {
		sx = strideX * -1;
		sy = strideY * -1;
		ox = offsetX - ( (N-1) * sx );
		oy = offsetY - ( (N-1) * sy );
	} else {
		sx = strideX;
		sy = strideY;
		ox = offsetX;
		oy = offsetY;
	}
	// Set the initial heap size:
	n = N;

	// Specify an initial "parent" index for building the heap:
	parent = stdlib_base_floor( N / 2 );

	// Continue looping until the array is sorted...
	while ( true ) {
		if ( parent > 0 ) {
			// We need to build the heap...
			parent -= 1;
			tx = X[ ox+(parent*sx) ];
			ty = Y[ oy+(parent*sy) ];
		} else {
			// Reduce the heap size:
			n -= 1;

			// Check if the heap is empty, and, if so, we are finished sorting...
			if ( n == 0 ) {
				return;
			}
			// Store the last heap value in a temporary variable in order to make room for the heap root being placed into its sorted position:
			ix = ox + (n*sx);
			tx = X[ ix ];
			iy = oy + (n*sy);
			ty = Y[ iy ];

			// Move the heap root to its sorted position:
			X[ ix ] = X[ ox ];
			Y[ iy ] = Y[ oy ];
		}
		// We need to "sift down", pushing `t` down the heap to in order to replace the parent and satisfy the heap property...

		// Start at the parent index:
		j = parent;

		// Get the "left" child index:
		child = (j*2) + 1;

		while ( child < n ) {
			// Find the largest child...
			k = child + 1;
			if ( k < n ) {
				v1 = X[ ox+(k*sx) ];
				v2 = X[ ox+(child*sx) ];

				// Check if a "right" child exists and is "bigger"...
				if ( v1 > v2 || stdlib_base_is_nanf( v1 ) || (v1 == v2 && stdlib_base_is_positive_zerof( v1 ) ) ) {
					child += 1;
				}
			}
			// Check if the largest child is bigger than `t`...
			v1 = X[ ox+(child*sx) ];
			if ( v1 > tx || stdlib_base_is_nanf( v1 ) || ( v1 == tx && stdlib_base_is_positive_zerof( v1 ) ) ) {
				// Insert the larger child value:
				X[ ox+(j*sx) ] = v1;
				Y[ oy+(j*sy) ] = Y[ oy+(child*sy) ];

				// Update `j` to point to the child index:
				j = child;

				// Get the "left" child index and repeat...
				child = (j*2) + 1;
			} else {
				// We've found `t`'s place in the heap...
				break;
			}
		}
		// Insert `t` into the heap:
		X[ ox+(j*sx) ] = tx;
		Y[ oy+(j*sy) ] = ty;
	}
}
