#include "stdlib/blas/ext/base/ssort2sh.h"
#include "stdlib/math/base/assert/is_negative_zerof.h"
#include "stdlib/math/base/assert/is_nanf.h"
#include "stdlib/strided/base/stride2offset.h"
#include "stdlib/blas/base/shared.h"
#include <stdbool.h>

/**
* Simultaneously sorts two single-precision floating-point strided arrays based on the sort order of the first array using Shellsort.
/**
*
* ## Notes
*
* -   This implementation uses the gap sequence proposed by Ciura (2001).
*
* ## References
*
* -   Shell, Donald L. 1959. "A High-Speed Sorting Procedure." _Communications of the ACM_ 2 (7). Association for Computing Machinery: 30–32. doi:[10.1145/368370.368387](https://doi.org/10.1145/368370.368387).
* -   Ciura, Marcin. 2001. "Best Increments for the Average Case of Shellsort." In _Fundamentals of Computation Theory_, 106–17. Springer Berlin Heidelberg. doi:[10.1007/3-540-44669-9\_12](https://doi.org/10.1007/3-540-44669-9_12).
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        first input array
* @param strideX  stride length for `x`
* @param Y        second input array
* @param strideY  stride length for `y`
*/
void API_SUFFIX(stdlib_strided_ssort2sh)( const CBLAS_INT N, const float order, float *X, const CBLAS_INT strideX, float *Y, const CBLAS_INT strideY ) {
	CBLAS_INT ox = stdlib_strided_stride2offset( N, strideX );
	CBLAS_INT oy = stdlib_strided_stride2offset( N, strideY );
	API_SUFFIX(stdlib_strided_ssort2sh_ndarray)( N, order, X, strideX, ox, Y, strideY, oy );
}


/**
* Simultaneously sorts two signle-precision floating-point strided arrays based on the sort order of the first array using Shellsort and alternative indexing semantics.
*
* @param N        number of indexed elements
* @param order    sort order
* @param X        first input array
* @param strideX  stride length for `x`
* @param offsetX  starting index for `x`
* @param Y        second input array
* @param strideY  stride length for `y`
* @param offsetY  starting index for `y`
*/
void API_SUFFIX(stdlib_strided_ssort2sh_ndarray)( const CBLAS_INT N, const float order, float *X, CBLAS_INT strideX, CBLAS_INT offsetX, float *Y, CBLAS_INT strideY, CBLAS_INT offsetY ) {
	CBLAS_INT gap;
	CBLAS_INT sx;
	CBLAS_INT sy;
	CBLAS_INT ox;
	CBLAS_INT oy;
	CBLAS_INT i;
	CBLAS_INT j;
	CBLAS_INT k;
	float vx;
	float vy;
	float ux;
	bool flg;

	// Ciura's gap sequence:
	const static CBLAS_INT GAPS[] = { 701, 301, 132, 57, 23, 10, 4, 1 };
	const static CBLAS_INT NGAPS = 8;

	if ( N <= 0 || order == 0.0f ) {
		return;
	}
	// For a positive stride, sorting in decreasing order is equivalent to providing a negative stride and sorting in increasing order, and, for a negative stride, sorting in decreasing order is equivalent to providing a positive stride and sorting in increasing order...
	if ( order < 0.0f ) {
		sx = -strideX;
		sy = -strideY;
		oy = offsetY - ( (N-1) * sy );
		ox = offsetX - ( (N-1) * sx );
	} else {
		sx = strideX;
		sy = strideY;
		ox = offsetX;
		oy = offsetY;
	}

	for ( i = 0; i < NGAPS; i++ ) {
		gap = GAPS[ i ];
		for ( j = gap; j < N; j++ ) {
			vx = X[ ox+(j*sx) ];

			// If `NaN`, the current value is already sorted to its place...
			if ( stdlib_base_is_nanf( vx ) ) {
				continue;
			}
			vy = Y[ oy+(j*sy) ];

			// Perform insertion sort on the "gapped" subarray...
			flg = stdlib_base_is_negative_zerof( vx );
			for ( k = j; k >= gap; k -= gap ) {
				ux = X[ ox+((k-gap)*sx) ];
				if ( ux <= vx && !(flg && ux == vx) ) {
					break;
				}
				X[ ox+(k*sx) ] = ux;
				Y[ oy+(k*sy) ] = Y[ oy+((k-gap)*sy) ];
			}
			X[ ox+(k*sx) ] = vx;
			Y[ oy+(k*sy) ] = vy;
		}
	}
	return;
}
