'use strict';

/**
* Allocate a buffer using an octet array.
*
* @module @stdlib/buffer/from-array
*
* @example
* var fromArray = require( '@stdlib/type/buffer/from-array' );
*
* var buf = fromArray( [ 1, 2, 3, 4 ] );
* // returns <Buffer>
*/

// MODULES //

var hasFrom = require( './has_from.js' );


// MAIN //

var fromArray;
if ( hasFrom ) {
	fromArray = require( './main.js' );
} else {
	fromArray = require( './polyfill.js' );
}


// EXPORTS //

module.exports = fromArray;
