'use strict';

// MODULES //

var isBuffer = require( '@stdlib/assert/is-buffer' );


// MAIN //

/**
* Returns a JSON representation of a `Buffer`.
*
* @param {Buffer} buffer - buffer to serialize
* @throws {TypeError} first argument must be a `Buffer`
* @returns {Object} JSON representation
*
* @example
* var array2buffer = require( '@stdlib/buffer/from-array' );
*
* var buf = array2buffer( [ 1, 2 ] );
* // returns <Buffer>
*
* var json = toJSON( buf );
* // returns { 'type': 'Buffer', 'data': [ 1, 2 ] }
*/
function toJSON( buffer ) {
	var out;
	var i;
	if ( !isBuffer( buffer ) ) {
		throw new TypeError( 'invalid input argument. Must provide a Buffer. Value: `' + buffer + '`.' );
	}
	out = {};
	out.type = 'Buffer';
	out.data = [];
	for ( i = 0; i < buffer.length; i++ ) {
		out.data.push( buffer[ i ] );
	}
	return out;
}


// EXPORTS //

module.exports = toJSON;
