'use strict';

// MODULES //

var tape = require( 'tape' );
var MAX_ARRAY_LENGTH = require( './../lib' );


// TESTS //

tape( 'main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MAX_ARRAY_LENGTH, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value is 4294967295', function test( t ) {
	t.strictEqual( MAX_ARRAY_LENGTH, 4294967295, 'returns expected value' );
	t.end();
});
