'use strict';

// MODULES //

var tape = require( 'tape' );
var pow = require( '@stdlib/math/base/special/pow' );
var FLOAT32_SMALLEST_NORMAL = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.equal( typeof FLOAT32_SMALLEST_NORMAL, 'number', 'main export is a number' );
	t.end();
});

tape( 'the exported value equals the smallest normal value', function test( t ) {
	t.equal( FLOAT32_SMALLEST_NORMAL, pow(2, -126), 'equals 2**-126' );
	t.end();
});
