'use strict';

// MODULES //

var tape = require( 'tape' );
var MILLISECONDS_IN_HOUR = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MILLISECONDS_IN_HOUR, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 1000*60*60=3600000', function test( t ) {
	t.strictEqual( MILLISECONDS_IN_HOUR, 1000*60*60, 'equals 1000*60*60=3600000' );
	t.end();
});
