'use strict';

// MODULES //

var tape = require( 'tape' );
var MILLISECONDS_IN_WEEK = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MILLISECONDS_IN_WEEK, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 1000*60*60*24*7=604800000', function test( t ) {
	t.strictEqual( MILLISECONDS_IN_WEEK, 1000*60*60*24*7, 'equals 1000*60*60*24*7=604800000' );
	t.end();
});
