'use strict';

// MODULES //

var tape = require( 'tape' );
var MINUTES_IN_HOUR = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof MINUTES_IN_HOUR, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 60', function test( t ) {
	t.strictEqual( MINUTES_IN_HOUR, 60, 'equals 60' );
	t.end();
});
