'use strict';

// MODULES //

var tape = require( 'tape' );
var SECONDS_IN_WEEK = require( './../lib' );


// TESTS //

tape( 'the main export is a number', function test( t ) {
	t.ok( true, __filename );
	t.strictEqual( typeof SECONDS_IN_WEEK, 'number', 'main export is a number' );
	t.end();
});

tape( 'the value equals 60*60*24*7=604800', function test( t ) {
	t.strictEqual( SECONDS_IN_WEEK, 60*60*24*7, 'equals 60*60*24*7=604800' );
	t.end();
});
