// TypeScript Version: 2.0

/* tslint:disable:max-line-length */
/* tslint:disable:max-file-line-count */

import arc4 = require( '@stdlib/crypto/arc4' );

/**
* Interface describing the `crypto` namespace.
*/
interface Namespace {
	/**
	* Returns an (alleged) RC4 stream cipher.
	*
	* @param key - cipher key
	* @returns stream cipher
	*
	* @example
	* var encrypt = ns.arc4( 'pwd12' );
	* var out = encrypt( 'beep boop bop' );
	* // returns '...'
	*
	* var decrypt = ns.arc4( 'pwd12' );
	* var str = decrypt( out );
	* // returns 'beep boop bop'
	*/
	arc4: typeof arc4;
}

/**
* Standard library cryptography.
*/
declare var ns: Namespace;


// EXPORTS //

export = ns;
