'use strict';

/**
* A list of English words rated for valence. An earlier version of AFINN-111.
*
* @module @stdlib/datasets/afinn-96
*
* @example
* var afinn96 = require( '@stdlib/datasets/afinn-96' );
* var words = afinn96();
* // returns [ ['abandon',-2], ['abandons',-2], ['abandoned',-2], ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var afinn96;
if ( IS_BROWSER ) {
	afinn96 = require( './browser.js' );
} else {
	afinn96 = require( './afinn_96.js' );
}


// EXPORTS //

module.exports = afinn96;
