'use strict';

// MODULES //

var data = require( './../data/words.json' );


// MAIN //

/**
* Returns a list of familiar American-English words.
*
* @returns {StringArray} words
*
* @example
* var list = words();
* // returns [ 'a', 'able', 'aboard', 'about', 'above', ... ]
*/
function words() {
	return data.slice();
}


// EXPORTS //

module.exports = words;
