'use strict';

/**
* A list of familiar American-English words.
*
* @module @stdlib/datasets/dale-chall-new
*
* @example
* var words = require( '@stdlib/datasets/dale-chall-new' );
*
* var data = words();
* // returns [ 'a', 'able', 'aboard', 'about', 'above', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var words;
if ( IS_BROWSER ) {
	words = require( './browser.js' );
} else {
	words = require( './dale_chall.js' );
}


// EXPORTS //

module.exports = words;
