'use strict';

// MODULES //

var data = require( './data.js' );
var string2buffer = require( '@stdlib/buffer/from-string' );


// MAIN //

/**
* Returns an image of Black Canyon.
*
* @returns {Buffer} image
*
* @example
* var img = image();
* // returns <Buffer>
*/
function image() {
	return string2buffer( data, 'base64' );
}


// EXPORTS //

module.exports = image;
