'use strict';

/**
* Image of Black Canyon as a `buffer` object.
*
* @module @stdlib/datasets/img-black-canyon
*
* @example
* var image = require( '@stdlib/datasets/img-black-canyon' );
*
* var img = image();
* // returns <Buffer>
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var image;
if ( IS_BROWSER ) {
	image = require( './browser.js' );
} else {
	image = require( './black_canyon.js' );
}


// EXPORTS //

module.exports = image;
