'use strict';

/**
* Image of an abandoned dust bowl home as a `buffer` object.
*
* @module @stdlib/datasets/img-dust-bowl-home
*
* @example
* var image = require( '@stdlib/datasets/img-dust-bowl-home' );
*
* var img = image();
* // returns <Buffer>
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var image;
if ( IS_BROWSER ) {
	image = require( './browser.js' );
} else {
	image = require( './dust_bowl_home.js' );
}


// EXPORTS //

module.exports = image;

