'use strict';

// MODULES //

var tape = require( 'tape' );
var isBuffer = require( '@stdlib/utils/is-buffer' );
var image = require( './../lib' );


// TESTS //

tape( 'main export is a buffer object', function test( t ) {
	t.ok( true, __filename );
	t.equal( isBuffer( image ), true, 'main export is a buffer object' );
	t.end();
});
