'use strict';

/**
* Twenty-four views of a house cat.
*
* @module @stdlib/datasets/img-locomotion-house-cat
*
* @example
* var image = require( '@stdlib/datasets/img-locomotion-house-cat' );
* var img = image();
* // returns <Buffer>
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var image;
if ( IS_BROWSER ) {
	image = require( './browser.js' );
} else {
	image = require( './locomotion_house_cat.js' );
}


// EXPORTS //

module.exports = image;
