'use strict';

// MODULES //

var tape = require( 'tape' );
var isArray = require( '@stdlib/utils/is-array' );
var list = require( './../lib' );


// TESTS //

tape( 'main export is an array of 2-element arrays', function test( t ) {
	var i;
	t.ok( true, __filename );
	t.equal( isArray( list ), true, 'main export is an array' );
	for ( i = 0; i < list.length; i++ ) {
		t.equal( isArray( list[i] ), true, 'element '+i+' is an array' );
		t.equal( list[i].length, 2, 'element '+i+' is a 2-element array' );
	}
	t.end();
});
