'use strict';

/**
* A list of positive opinion words.
*
* @module @stdlib/datasets/liu-positive-opinion-words-en
*
* @example
* var words = require( '@stdlib/datasets/liu-positive-opinion-words-en' );
* var list = words();
* // returns [ 'a+', 'abound', 'abounds', 'abundance', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var words;
if ( IS_BROWSER ) {
	words = require( './browser.js' );
} else {
	words = require( './liu_positive_opinion_words_en.js' );
}


// EXPORTS //

module.exports = words;
