	var words;
	var d1;
	var d2;
	var v;

	words = proxyquire( './../lib', {
		'@stdlib/assert/is-browser': false
	});

	d1 = words();
	d2 = words();

	t.notEqual( d1, d2, 'different references' );

	v = d2[ 5 ];
	d1[ 5 ] = 'beep';

	t.equal( d1[ 5 ], 'beep', 'expected element' );
