'use strict';

/**
* The text of Moby Dick by Herman Melville.
*
* @module @stdlib/datasets/moby-dick
*
* @example
* var text = require( '@stdlib/datasets/moby-dick' );
*
* var data = text();
* // returns [ {...}, {...}, ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var text;
if ( IS_BROWSER ) {
	text = require( './browser.js' );
} else {
	text = require( './moby_dick.js' );
}


// EXPORTS //

module.exports = text;
