'use strict';

/**
* Dataset for Nightingale's famous polar area diagram.
*
* @module @stdlib/datasets/nightingales-rose
*
* @example
* var rose = require( '@stdlib/datasets/nightingales-rose' );
* var data = rose();
* // returns [ {...}, {...}, ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var rose;
if ( IS_BROWSER ) {
	rose = require( './browser.js' );
} else {
	rose = require( './nightingales_rose.js' );
}


// EXPORTS //

module.exports = rose;
