'use strict';

/**
* A list of Finnish stop words.
*
* @module @stdlib/datasets/savoy-stopwords-fin
*
* @example
* var stopwords = require( '@stdlib/datasets/savoy-stopwords-fin' );
* var list = stopwords();
* // returns [ 'aiemmin', 'aika', 'aikaa', 'aikaan', 'aikaisemmin', ... ]
*/

// MODULES //

var IS_BROWSER = require( '@stdlib/assert/is-browser' );


// MAIN //

var stopwords;
if ( IS_BROWSER ) {
	stopwords = require( './browser.js' );
} else {
	stopwords = require( './savoy_stopwords_fin.js' );
}


// EXPORTS //

module.exports = stopwords;
