'use strict';

// MODULES //

var data = require( './../data/words.json' );


// FINNISH STOPWORDS //

/**
* Returns a list of Finnish stop words.
*
* @returns {StringArray} stop words
*
* @example
* var list = stopwords();
* // returns [ 'aiemmin', 'aika', 'aikaa', 'aikaan', 'aikaisemmin', ... ]
*/
function stopwords() {
	return data.slice();
} // end FUNCTION stopwords()


// EXPORTS //

module.exports = stopwords;
